CREATE TABLE `bundle_items` (
  `bundle_item_id` int(8) NOT NULL AUTO_INCREMENT,
  `bundle_id` int(8) NOT NULL,
  `item_id` int(4) NOT NULL,
  PRIMARY KEY (`bundle_item_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

CREATE TABLE `bundle_menus` (
  `bundle_menu_id` int(11) NOT NULL AUTO_INCREMENT,
  `target_bundle_menu` int(4) NOT NULL,
  PRIMARY KEY (`bundle_menu_id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8mb4;

CREATE TABLE `bundle_menu_items` (
  `bundle_menu_item_id` int(8) NOT NULL,
  `bundle_menu_id` int(4) NOT NULL,
  `item_id` int(4) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

CREATE TABLE `categories` (
  `category_id` int(2) NOT NULL AUTO_INCREMENT,
  `category_name` varchar(64) NOT NULL,
  PRIMARY KEY (`category_id`)
) ENGINE=InnoDB AUTO_INCREMENT=14 DEFAULT CHARSET=utf8mb4 ROW_FORMAT=COMPACT;

CREATE TABLE `category_bundles` (
  `id` int(8) NOT NULL AUTO_INCREMENT,
  `cat_id` int(8) NOT NULL,
  `bundle_id` int(8) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

CREATE TABLE `category_categories` (
  `id` int(8) NOT NULL AUTO_INCREMENT,
  `parent_cat_id` int(8) NOT NULL,
  `child_cat_id` int(8) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=14 DEFAULT CHARSET=utf8mb4;

CREATE TABLE `category_items` (
  `id` int(8) NOT NULL AUTO_INCREMENT,
  `cat_id` int(8) NOT NULL,
  `item_id` int(8) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=65 DEFAULT CHARSET=utf8mb4 ROW_FORMAT=COMPACT;

CREATE TABLE `category_positions` (
  `category_position_id` int(4) NOT NULL AUTO_INCREMENT,
  `cat_id` int(4) NOT NULL,
  `pos_id` int(4) NOT NULL,
  PRIMARY KEY (`category_position_id`)
) ENGINE=InnoDB AUTO_INCREMENT=13 DEFAULT CHARSET=utf8mb4;

CREATE TABLE `item_codes` (
  `item_code_id` int(4) NOT NULL AUTO_INCREMENT,
  `code_id` int(4) NOT NULL,
  `item_id` int(4) DEFAULT NULL,
  PRIMARY KEY (`item_code_id`)
) ENGINE=InnoDB AUTO_INCREMENT=95 DEFAULT CHARSET=utf8mb4;

CREATE TABLE `positions` (
  `position_id` int(4) NOT NULL AUTO_INCREMENT,
  `position_name` varchar(8) NOT NULL,
  PRIMARY KEY (`position_id`)
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8mb4;
